import React, {useEffect, useState} from 'react'
import Navbar from "react-bootstrap/Navbar";
import NavDropdown from "react-bootstrap/NavDropdown";
import Nav from "react-bootstrap/Nav";
import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";
import Form from 'react-bootstrap/Form';
import FormControl from 'react-bootstrap/FormControl';
import 'bootstrap-icons/font/bootstrap-icons.css';
import './navBar.css'

const NavigationBar = (onChange, value, onClick ) => {
  const [city, setCity] = useState('')
  const [weatherData, setWeatherData] = useState({})

  const API_KEY = '012d92d069b98661575b2b375a890b05';
  
  let params = new URLSearchParams({
    access_key: API_KEY,
    query: city,
    units: 'm'
  }, [])

  const getWeather = (event) => {
      fetch(`http://api.weatherstack.com/current?${params}`).then(response => response.json())
        .then(data => {
          setWeatherData(data)
          console.log(data)
        }
      )
  }

  return (
    <Navbar className = " pl-3 justify-content-center top-nav-bar navbar-dark" expand="lg">
        <Container fluid>
            
            <Navbar.Brand href="#" className='pl-2'>Forecast</Navbar.Brand>
            <Navbar.Toggle aria-controls="navbarScroll" />
            <Navbar.Collapse id="navbarScroll">
            <Nav
                className="m-auto my-lg-0 text-center"
                style={{ maxHeight: '100px' }}
                navbarScroll>
                
                <Nav.Link href="#" className = "pl-5" ><i className="bi bi-arrow-clockwise"></i></Nav.Link>
                <Nav.Link href="#" ><i className="bi bi-star"></i></Nav.Link>
                <Nav.Link href="#" ><i className="bi bi-moon-fill"></i></Nav.Link>

                <NavDropdown title="Account" id="basic-nav-dropdown">
                <NavDropdown.Item href="#action/3.1">Account detailes</NavDropdown.Item>
                <NavDropdown.Item href="#action/3.1">Settings</NavDropdown.Item>
                <NavDropdown.Divider />
                <NavDropdown.Item href="#action/3.2">Show favorites</NavDropdown.Item>
                </NavDropdown>

            </Nav>
            <Form className="d-flex">
                <FormControl
                type="search"
                placeholder="Search"
                className="me-2 bg-secondary bg-gradient text-light searchBar"
                aria-label="Search"
                value = {city}
                onChange = {event => setCity(event.target.value)}
                
                />
                <Button 
                  onClick = {getWeather}
                  className = "btn btn-secondary">Search
                </Button>
            </Form>
            </Navbar.Collapse>
        </Container>
    </Navbar>
  )
}

export default NavigationBar
