import React, {useEffect, useState} from "react";
import 'bootstrap/dist/css/bootstrap.min.css'
import NavigationBar from "./components/NavigationBar";
import SideBar from "./components/SideBar"
import Main from "./components/Main"



function App() {

  const API_KEY = '012d92d069b98661575b2b375a890b05';

  const [weatherData, setWeatherData] = useState([{}])

  let params = new URLSearchParams({
    access_key: API_KEY,
    query: 'New York',
    units: 'm'
  }, [])

  const getWeather = (event) => {
    if(event.key == "Enter") {
      fetch(`http://api.weatherstack.com/current?${params}`).then(response => response.json())
        .then(data => {
          setWeatherData(data)
        }
      )
    }
  }

  useEffect(() => {
    const getWeather = async() => {
    const api_call = await fetch(`http://api.weatherstack.com/current?${params}`);
    const data = await api_call.json();

    console.log(data);
    }

    getWeather()
  });

  return (
    <div className="App">
      <NavigationBar />
      <SideBar/>
      <Main />
    </div>
  );

}

export default App;
