import React, {useEffect, useState} from "react";
import 'bootstrap/dist/css/bootstrap.min.css'
import NavigationBar from "./components/NavigationBar";
import SideBar from "./components/SideBar"
import Main from "./components/Main"



function App() {

  const API_KEY = '012d92d069b98661575b2b375a890b05';

  const [weatherData, setWeatherData] = useState([{}])
  const [city, setCity] = useState('')

  let params = new URLSearchParams({
    access_key: API_KEY,
    query: city,
    units: 'm'
  }, [])

  const getWeather = (event) => {
    if(event.key == "Enter") {
      fetch(`http://api.weatherstack.com/current?${params}`).then(response => response.json())
        .then(data => {
          setWeatherData(data)
        }
      )
    }
  }


  return (
    <div className="App">
      <NavigationBar onChange = {e => setCity(e.target.value)} value = {city} onClick = {getWeather}/>
      <SideBar/>
      <Main />
    </div>
  );

}

export default App;
