import React, {useEffect, useState} from "react";
import 'bootstrap/dist/css/bootstrap.min.css'
import NavigationBar from "./components/NavigationBar";
import SideBar from "./components/SideBar"
import Main from "./components/Main"

const API_KEY = '012d92d069b98661575b2b375a890b05';

function App() {
  const [data, getWeather] = useState()

  let params = new URLSearchParams({
    access_key: API_KEY,
    query: 'New York',
    units: 'm'
  })

  useEffect(() => {
    const getWeather = async() => {
    const api_call = await fetch(`http://api.weatherstack.com/current?${params}`);
    const data = await api_call.json();

    return(data);
    }

    getWeather();
  });

  return (
    <div className="App">
      <NavigationBar/>
      <SideBar/>
      <Main />
    </div>
  );

}

export default App;
